import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Alert } from '@mui/material';

const LoginPage: React.FC = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const { email, password } = formData;

    if (!email || !password) {
      setError('모든 필드를 입력해주세요.');
      return;
    }

    console.log('로그인 요청:', { email, password });
    setError('');
    alert('로그인 성공!');
  };

  return (
    <Box
      sx={{
        display: 'flex',
        alignItems: 'center', // 수직 중앙 정렬
        justifyContent: 'center', // 수평 중앙 정렬
        minHeight: '100vh', // 전체 화면 높이 사용
        backgroundColor: '#fff', // 전체 배경 하얀색
      }}
    >
      <Box
        sx={{
          width: '100%',
          maxWidth: 400, // 최대 너비 400px
          padding: 3,
          backgroundColor: '#fff', // 로그인 창 배경도 하얀색
          borderRadius: 2,
          boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // 그림자 효과
          textAlign: 'center',
        }}
      >
        <Typography variant="h4" sx={{ mb: 2, fontWeight: 'bold', color: '#4a90e2' }}>
          ACCOUNT LOGIN
        </Typography>
        <Box
          component="form"
          onSubmit={handleSubmit}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            gap: 2,
          }}
        >
          <TextField
            label="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            variant="outlined"
            fullWidth
            required
          />
          <TextField
            label="Password"
            name="password"
            type="password"
            value={formData.password}
            onChange={handleChange}
            variant="outlined"
            fullWidth
            required
          />
          {error && <Alert severity="error">{error}</Alert>}
          <Button type="submit" variant="contained" color="primary" fullWidth>
            LOG IN
          </Button>
        </Box>
      </Box>
    </Box>
  );
};

export default LoginPage;
