import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  Drawer,
} from '@mui/material';

// API 호출 시 받아올 라켓 데이터 타입 정의
interface Racquet {
  id: number;
  name: string;
  image: string;
  brand: string;
}

const RacquetPage: React.FC = () => {
  const { brand } = useParams<{ brand: string }>(); // URL 파라미터로 전달받은 브랜드명
  const [racquets, setRacquets] = useState<Racquet[]>([]);

  // 좌측 브랜드 리스트
  const brandList = ['Wilson', 'Babolat', 'Head', 'Prince', 'Yonex'];

  // 브랜드 클릭 시 API 호출
  const fetchRacquets = async (brandName: string) => {
    try {
      // 예시 API 호출 (백엔드 구현 필요)
      const response = await fetch(`/api/racquets?brand=${brandName}`);
      const data = await response.json();
      setRacquets(data); // 라켓 데이터 상태 업데이트
    } catch (error) {
      console.error('Failed to fetch racquets:', error);
    }
  };

  useEffect(() => {
    if (brand) {
      fetchRacquets(brand); // 페이지 로드 시 해당 브랜드의 라켓 데이터 가져오기
    }
  }, [brand]);

  return (
    <Box sx={{ display: 'flex' }}>
      {/* 좌측 브랜드 리스트 */}
      <Drawer
        variant="permanent"
        anchor="left"
        sx={{
          width: 200,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 200,
            boxSizing: 'border-box',
            top: 64, // Header 높이에 맞춤
          },
        }}
      >
        <List>
          {brandList.map((brandName) => (
            <ListItem key={brandName} disablePadding>
              <ListItemButton onClick={() => fetchRacquets(brandName)}>
                <ListItemText primary={brandName} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>

      {/* 메인 컨텐츠 */}
      <Box sx={{ flex: 1, textAlign: 'center', p: 4, ml: '200px' }}>
        {/* 브랜드명 헤더 */}
        <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold' }}>
          {brand ? `${brand.charAt(0).toUpperCase()}${brand.slice(1)}` : 'All'} Tennis Racquets
        </Typography>
        <Typography variant="body1" sx={{ mb: 4 }}>
          To speak with a racquet specialist please call{' '}
          <a href="tel:1-800-799-9230">1-800-799-9230</a>
        </Typography>

        {/* 라켓 목록 */}
        <Grid container spacing={4} justifyContent="center">
          {racquets.map((racquet) => (
            <Grid item key={racquet.id} xs={12} sm={6} md={4} lg={3}>
              <Card>
                <CardActionArea>
                  {/* 라켓 이미지 */}
                  <CardMedia
                    component="img"
                    image={racquet.image}
                    alt={racquet.name}
                    sx={{ height: 200, objectFit: 'contain', p: 2 }}
                  />
                  {/* 라켓 정보 */}
                  <CardContent>
                    <Typography variant="subtitle1" textAlign="center" gutterBottom>
                      {racquet.name}
                    </Typography>
                    <Typography variant="body2" color="textSecondary" textAlign="center">
                      {racquet.brand} Tennis Racquets
                    </Typography>
                  </CardContent>
                </CardActionArea>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
};

export default RacquetPage;
